import React, { Fragment } from "react";
import { useForm } from "react-hook-form";
import {
  Avatar,
  Button,
  CssBaseline,
  TextField,
  Typography,
  makeStyles,
  Container,
  Paper,
  Grid,
} from "@material-ui/core";

// import "./Styles.css";
const useStyles = makeStyles((theme) => ({
  paper: {
    width: "160%",
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },

  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  btn: {
    background: "linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)",
    border: 0,
    borderRadius: 3,
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
    color: "white",
    height: 48,
    padding: "0 30px",
  },
}));

function NewTest() {
  const classes = useStyles();

  const [indexes, setIndexes] = React.useState([]);
  const [counter, setCounter] = React.useState(0);
  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    const team = [];
    const { friends } = data;
    console.log(friends);
    friends.map((friend) => team.push(friend.email));
    console.log(team);
  };

  const addFriend = () => {
    setIndexes((prevIndexes) => [...prevIndexes, counter]);
    setCounter((prevCounter) => prevCounter + 1);
  };

  const removeFriend = (index) => () => {
    setIndexes((prevIndexes) => [
      ...prevIndexes.filter((item) => item !== index),
    ]);
    setCounter((prevCounter) => prevCounter - 1);
  };

  const clearFriends = () => {
    setIndexes([]);
  };

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Paper elevation={3} className={classes.paper}>
        <Avatar className={classes.avatar}>{/* <LockOutlinedIcon /> */}</Avatar>
        <Typography component="h1" variant="h5">
          Invite your team
        </Typography>
        <form
          onSubmit={handleSubmit(onSubmit)}
          className={classes.form}
          noValidate
        >
          {indexes.map((index) => {
            const fieldName = `friends[${index}]`;
            return (
              <Fragment key={fieldName}>
                <TextField
                  inputRef={register({
                    required: true,
                    minLength: 3,
                    validate: (value) => {
                      return !!value.trim();
                    },
                  })}
                  type="text"
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name={`${fieldName}.email`}
                  autoComplete="email"
                />
                {errors.email && (
                  <p style={{ color: "crimson" }}>Please enter a valid email</p>
                )}
                <button
                  className={classes.btn}
                  type="button"
                  onClick={removeFriend(index)}
                >
                  Remove
                </button>
              </Fragment>
            );
          })}
          <div>
            <button className={classes.btn} type="button" onClick={addFriend}>
              Add email
            </button>
          </div>

          <button className={classes.btn} type="button" onClick={clearFriends}>
            Clear Team
          </button>
          <input className={classes.btn} type="submit" />
        </form>
      </Paper>
    </Container>
  );
}

export default NewTest;
