import React, { useState, Fragment } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../actions/auth";
import PropTypes from "prop-types";
import { makeStyles, useTheme } from "@material-ui/styles";
import bear from "../imgs/bear.png";
// import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import {
  Paper,
  Tab,
  AppBar,
  Toolbar,
  Tabs,
  useScrollTrigger,
  useMediaQuery,
  SwipeableDrawer,
  IconButton,
  List,
  ListItem,
  ListItemText,
} from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";

function ElevationScroll(props) {
  const { children, window } = props;

  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
    target: window ? window() : undefined,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

const useStyles = makeStyles((theme) => ({
  toolbarMargin: {
    ...theme.mixins.toolbar,
    marginBottom: "0.5em",
    [theme.breakpoints.down("xs")]: {
      marginBottom: "1.5em",
    },
  },
  logo: {
    marginLeft: "30px",
    height: "2.5em",
    width: "4.5em",

    [theme.breakpoints.down("xs")]: {
      marginLeft: "15px",
      height: "1.8em",
      width: "3.2em",
    },
  },
  fixit: {
    margin: "0",
  },
  tabContainer: {
    marginLeft: "auto",
  },
  tab: {
    fontWeight: 700,
    minWidth: 100,
    marginLeft: "25px",
  },
  DrawerIconContainer: {
    marginLeft: "auto",
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  drawerIcon: {
    height: "50px",
    width: "35px",
  },
  drawer: {
    backgroundColor: "rgb(217, 226, 226)",
  },
  drawerItem: {
    fontSize: "1.5em",
  },
}));

function Navbar({ isAuthenticated, loading, logout, username }) {
  const theme = useTheme();
  const matches = useMediaQuery(theme.breakpoints.down("md"));
  // const matchesXs = useMediaQuery(theme.breakpoints.down("xs"));
  const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent);

  //To fix the indication tab on refresh
  const currentTab = () => {
    let path = window.location.pathname;
    if (path === "/") return 0;
    else if (path === "/about") return 1;
    else if (path === "/contact") return 2;
    else return 0;
  };

  const [value, setValue] = useState(currentTab);
  const [openDrawer, setOpenDrawer] = useState(false);

  const handleChange = (e, newValue) => {
    setValue(newValue);
  };

  const classes = useStyles();
  const tabs = (
    <Fragment>
      <Tabs
        className={classes.tabContainer}
        value={value}
        onChange={handleChange}
        indicatorColor="primary"
      >
        <Tab component={Link} to="/" className={classes.tab} label="Home" />

        {!loading && isAuthenticated && (
          <Tab
            component={Link}
            to="/dashboard"
            className={classes.tab}
            label="Dashboard"
          />
        )}
        {!loading && isAuthenticated && (
          <Tab
            component={Link}
            to="/dashboard"
            className={classes.tab}
            label={`Hi ${username}`}
          />
        )}
        {!loading && isAuthenticated && (
          <Tab
            onClick={() => {
              logout();
              setValue(0);
            }}
            component={Link}
            to="/login"
            className={classes.tab}
            label="Logout"
          />
        )}
      </Tabs>
    </Fragment>
  );

  const drawer = (
    <Fragment>
      <SwipeableDrawer
        disableBackdropTransition={!iOS}
        disableDiscovery={iOS}
        open={openDrawer}
        classes={{ paper: classes.drawer }}
        onClose={() => setOpenDrawer(false)}
        onOpen={() => setOpenDrawer(true)}
      >
        <List disablePadding>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(0);
            }}
            divider
            button
            component={Link}
            to="/"
            selected={value === 0}
          >
            <ListItemText className={classes.drawerItem} disableTypography>
              Home
            </ListItemText>
          </ListItem>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(1);
            }}
            divider
            button
            component={Link}
            to="/about"
            selected={value === 1}
          >
            <ListItemText className={classes.drawerItem} disableTypography>
              About
            </ListItemText>
          </ListItem>
          <ListItem
            onClick={() => {
              setOpenDrawer(false);
              setValue(2);
            }}
            divider
            button
            component={Link}
            to="/contact"
            selected={value === 2}
          >
            <ListItemText className={classes.drawerItem} disableTypography>
              Contact
            </ListItemText>
          </ListItem>
          {!loading && isAuthenticated && (
            <ListItem
              onClick={() => {
                setOpenDrawer(false);
              }}
              // onClick={logout}
              divider
              button
              component={Link}
              to="/dashboard"
              // selected={value === 3}
            >
              <ListItemText className={classes.drawerItem} disableTypography>
                Dashboard
              </ListItemText>
            </ListItem>
          )}
          {!loading && isAuthenticated && (
            <ListItem
              onClick={() => {
                setOpenDrawer(false);
                logout();
              }}
              // onClick={logout}
              divider
              button
              component={Link}
              to="/login"
              // selected={value === 3}
            >
              <ListItemText className={classes.drawerItem} disableTypography>
                Logout
              </ListItemText>
            </ListItem>
          )}
        </List>
      </SwipeableDrawer>
      <IconButton
        className={classes.DrawerIconContainer}
        onClick={() => setOpenDrawer(!openDrawer)}
        disableRipple
      >
        <MenuIcon className={classes.drawerIcon} />
      </IconButton>
    </Fragment>
  );
  return (
    <React.Fragment>
      <ElevationScroll>
        <AppBar position="fixed">
          <Paper elevation={10}>
            <Toolbar>
              <Link to="/" style={{ textDecoration: "none" }}>
                <img src={bear} className={classes.logo} alt="logo" />
                {/* {matchesXs ? (
                  <h2 className={classes.fixit}>FixIt</h2>
                ) : (
                  <h1 className={classes.fixit}>FixIt</h1>
                )} */}
              </Link>

              {matches ? drawer : tabs}
            </Toolbar>
          </Paper>
        </AppBar>
      </ElevationScroll>
      <div className={classes.toolbarMargin} />
    </React.Fragment>
  );
}

Navbar.propTypes = {
  isAuthenticated: PropTypes.bool,
  loading: PropTypes.bool,
  logout: PropTypes.func.isRequired,
};

function mapStateToProps(state) {
  const { isAuthenticated, loading } = state.auth;
  const { username } = state.auth;

  return {
    isAuthenticated,
    loading,
    username,
  };
}
export default connect(mapStateToProps, { logout })(Navbar);
