import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProjects } from "../actions/projects";
import PropTypes from "prop-types";
import { LinearProgress } from "@material-ui/core";

const Bug = ({ projectId, bugId, bug, getProjects }) => {
  //   const classes = useStyles();

  useEffect(() => {
    getProjects();
  }, []);

  const onClick = () => {
    console.log(bugId, projectId, bug);
  };

  if (!bug) {
    return (
      <div>
        <h1>Loading ...</h1>
        <LinearProgress color="secondary" />
      </div>
    );
  }

  return (
    <div>
      <button onClick={onClick}>Click me</button>
    </div>
  );
};

Bug.propTypes = {};

function mapStateToProps(state, { match }) {
  const { projectId, bugId } = match.params;
  const { yourProjects } = state.projects;
  const project = yourProjects[projectId];
  const bugs = project.bugs;
  const bug = bugs[bugId];

  return {
    projectId,
    bugId,
    bug,
    project,
  };
}

export default connect(mapStateToProps, { getProjects })(Bug);
