import React, { useEffect } from "react";
import { getProjects } from "../actions/projects";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import noProjects from "../imgs/noProjects.svg";
import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  container: {
    margin: "auto",
    textAlign: "center",
  },
  img: {
    marginRight: "2rem",
    height: "30rem",
    width: "30rem",
  },
  h3: {
    fontFamily: "helvetica",
  },
}));

function Dashboard({ getProjects, yourProjects, loading }) {
  const classes = useStyles();

  useEffect(() => {
    getProjects();
  }, []);

  if (yourProjects === false) {
    return (
      <div className={classes.container}>
        <img className={classes.img} src={noProjects} />
        <p className={classes.h3}>
          <strong>
            You are currently not a member of any projects!...
            <br />
            Create a new one.
          </strong>
        </p>
      </div>
    );
  }
  return <div>{JSON.stringify(yourProjects)}</div>;
}

Dashboard.propTypes = {
  getProjects: PropTypes.func.isRequired,
};

function mapStateToProps(state) {
  const { yourProjects, loading } = state.projects;

  return {
    yourProjects,
    loading,
  };
}

export default connect(mapStateToProps, { getProjects })(Dashboard);
