import React, { useEffect, useState } from "react";
import { useForm, Controller } from "react-hook-form";
import Select from "react-select";
import PropTypes from "prop-types";
import bugImage from "../imgs/bug.png";
import { connect } from "react-redux";
import { getProjects } from "../actions/projects";

import {
  Avatar,
  Button,
  CssBaseline,
  TextField,
  Typography,
  makeStyles,
  Container,
  Grid,
  Paper,
} from "@material-ui/core";

const statusList = [
  { name: "Open" },
  { name: "In progress" },
  { name: "Closed" },
];

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "3rem",
    paddingBottom: "8rem",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  img: {
    height: "4rem",
    width: "4rem",
  },
}));

const NewBug = ({ projectId, email, getProjects, teamMembers }) => {
  useEffect(() => {
    getProjects();
  }, []);
  const [assignedTo, setAssignedTo] = useState("");
  const classes = useStyles();
  const { register, handleSubmit, errors, getValues, control } = useForm();

  const handleChange = () => {};

  const handleOnSubmit = (data) => {
    console.log(data);
  };
  if (!teamMembers) {
    return <h1>Loading...</h1>;
  }

  return (
    <Container component="main" maxWidth="sm">
      <CssBaseline />
      <Paper elevation={3} className={classes.paper}>
        <img className={classes.img} src={bugImage} />
        <Typography component="h1" variant="h5">
          New bug
        </Typography>
        <form
          onSubmit={handleSubmit(handleOnSubmit)}
          className={classes.form}
          noValidate
        >
          <TextField
            inputRef={register({
              required: true,
              minLength: 3,
              validate: (value) => {
                return !!value.trim();
              },
            })}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="title"
            label="Title"
            name="title"
            autoFocus
          />
          {errors.title && (
            <p style={{ color: "crimson" }}>
              Title should be at least 3 characters
            </p>
          )}
          <TextField
            inputRef={register({
              validate: (value) => {
                return !!value.trim();
              },
            })}
            variant="outlined"
            margin="normal"
            required
            multiline
            fullWidth
            rows={3}
            id="expectedResult"
            label="Expected result"
            name="expectedResult"
          />
          {errors.expectedResult && (
            <p style={{ color: "crimson" }}>Please enter expected result</p>
          )}
          <TextField
            inputRef={register({
              validate: (value) => {
                return !!value.trim();
              },
            })}
            variant="outlined"
            margin="normal"
            fullWidth
            multiline
            rows={3}
            name="actualresult"
            label="Actual result"
            type="text"
            id="actualResult"
          />
          {errors.actualResult && (
            <p style={{ color: "crimson" }}>Please enter actual result</p>
          )}
          <Controller
            required
            as={Select}
            options={statusList}
            getOptionLabel={(option) => option.name}
            getOptionValue={(option) => option.name}
            onChange={([option]) => option.name}
            name="status"
            control={control}
          />

          <select
            onChange={(e) => this.handleChange(e)}
            placeholder="Assigned to"
          >
            <option value="null">Select</option>
            {teamMembers.map((user, index) => (
              <option key={index} value={user}>
                {user}
              </option>
            ))}
          </select>

          <Button
            type="submit"
            fullWidth
            variant="contained"
            style={{ backgroundColor: "#1976d2", color: "white" }}
            className={classes.submit}
          >
            Add
          </Button>
        </form>
      </Paper>
    </Container>
  );
};

NewBug.propTypes = {};
function mapStateToProps(state, { match }) {
  const { projectId } = match.params;
  const { auth, projects } = state;
  const { yourProjects } = projects;
  let teamMembers;
  if (yourProjects[projectId]) {
    teamMembers = yourProjects[projectId].teamMembers;
  }
  let email;
  if (auth.email) {
    email = auth.email;
  }
  return {
    email,
    projectId,
    teamMembers,
  };
}

export default connect(mapStateToProps, { getProjects })(NewBug);
