import React, { useEffect } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";

const Bug = ({ projectId, bugId, bug }) => {
  const classes = useStyles();

  useEffect(() => {
    getProjects();
  }, []);

  if (!bug) {
    return (
      <div>
        <h1>Loading ...</h1>
        <LinearProgress color="secondary" />
      </div>
    );
  }

  const onClick = () => {
    console.log(bugId, projectId, bug);
  };
  return (
    <div>
      <button onClick={onClick}>Click me</button>
    </div>
  );
};

Bug.propTypes = {};

function mapStateToProps(state, { match }) {
  const { projectId, bugId } = match.params;
  const { yourProjects } = state.projects;
  const bug = yourProjects[projectId][bugId];

  return {
    projectId,
    bugId,
    bug,
  };
}

export default connect(mapStateToProps)(Bug);
