import {
  GET_PROJECTS,
  PROJECTS_ERROR,
  CLEAR_PROJECTS,
} from "../actions/projects";

const initialState = {
  projects: [],
  loading: true,
  noProjects: null,
};

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case GET_PROJECTS:
      return {
        ...state,
        projects: payload,
        loading: false,
      };
    case PROJECTS_ERROR:
      return {
        ...state,
        noProjects: true,
        loading: false,
      };

    case CLEAR_PROJECTS:
      return {
        ...state,
        projects: [],
        noProjects: null,
        loading: true,
      };
    default:
      return state;
  }
}
