import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import { FormControl, Typography, Fab } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import InputAdornment from "@material-ui/core/InputAdornment";
import Tooltip from "@material-ui/core/Tooltip";

const Test1 = () => {
  const [value, setValue] = useState(["0"]);
  function appendInput() {
    console.log(typeof value);
    // const newInput = `${value.legth()}`;
    setValue((value) => [...value, "0"]);
  }
  return (
    <div>
      {value.map((input, index) => (
        <Grid key={index} xs={12} container spacing={1} item>
          <Grid xs={3} item>
            <FormControl fullWidth margin="dense">
              <TextField
                variant="outlined"
                required
                type="number"
                id={input + "-mobile"}
                label="Email"
                name={input + "-mobile"}
                size="small"
                className="mobile"
                InputProps={{
                  endAdornment: index + 1 === value.length && (
                    <InputAdornment position="start">
                      <Tooltip title="Add Statement">
                        <Fab
                          color="primary"
                          size="small"
                          onClick={() => appendInput()}
                        >
                          <AddIcon />
                        </Fab>
                      </Tooltip>
                    </InputAdornment>
                  ),
                }}
              />
            </FormControl>
          </Grid>
        </Grid>
      ))}
    </div>
  );
};

export default Test1;
